<?php
function hindi_digits( $arabic_digits ) { 
    $indic_digits = array(
        "\xD9\xA0",
        "\xD9\xA1",
        "\xD9\xA2",
        "\xD9\xA3",
        "\xD9\xA4",
        "\xD9\xA5",
        "\xD9\xA6",
        "\xD9\xA7",
        "\xD9\xA8",
        "\xD9\xA9",
    );

    return str_replace(array_keys($indic_digits), $indic_digits, $arabic_digits);
};
?>
