please guys i need some help over here as this is js function for zoom in/out and not working 

  var svg = document.getElementById('egypt-map'),
         zoomIn= document.getElementById('zoom-in'),
          zoomOut=document.getElementById('zoom-out');

function initZooming(count){
if(count <= 1 ){
    zoomIn.onclick=function(){
        return count++;
        document.getElementById('demo').innerHTML = count;
        svg.style.transform="scale("+ count + ")";
        if(count >= 4){
            this.setAttribute('onclick','return false')
        }
    }

}else if(count > 1){
    zoomOut.onclick=function(){
        return count--;
        document.getElementById('demo').innerHTML = count;
        svg.style.transform="scale("+ count + ")";  
    }
}
}
initZooming(1)
